package operators;

public class IbcrementAndDecrementOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int l = 5;
		l++;
		System.out.println(l);
		l--;
		System.out.println(l);
		
		int r=23;
		int s;
		s=r++;
		System.out.println(s);
		
		int y=23;
		int n;
		n=--y;
		System.out.println(n);

	}

}
